	WHERE DOCUMENTTYPE_ID in (select id from DOCUMENTTYPE where name='UAT Local - Proces verbal CD') 
	    or COLLECTION_ID in (select id from METADATACOLLECTION where documenttype_id in (select id from DOCUMENTTYPE where name='UAT Local - Proces verbal CD'));
