changeSet.sql:
CREATE TABLE USERS (
ID BIGINT NOT NULL, EMAIL TEXT, EMPLOYEENUMBER TEXT, FIRSTNAME TEXT, LASTNAME TEXT, PASSWORD TEXT, USERNAME TEXT NOT NULL, MOBILE TEXT,
CONSTRAINT EDOCUSER_PK PRIMARY KEY (ID) );
CREATE TABLE PARAMETERS ( ID BIGINT NOT NULL, NAME TEXT NOT NULL, DESCRIPTION TEXT NOT NULL, VALUE TEXT NOT NULL, TYPE TEXT NOT NULL,
CONSTRAINT PARAMETERS_PK PRIMARY KEY(ID) );
