changeSet.sql:
UPDATE documenttypetemplate 
SET export_availability_expression='{aprobare_bom} =~ ''.*#aprobat#.*'' or {aprobare_superior} =~ ''.*#aprobat#.*'' or {aprobare_pe_bom} =~ ''.*#aprobat#.*'''
WHERE documenttypeid = (select id from documenttype where name = 'Cerere concediu');

UPDATE documenttypetemplate 
SET export_availability_expression='({aprobare_bom} =~ ''.*#respins#.*'' or {aprobare_superior} =~ ''.*#respins#.*'' or {aprobare_pe_bom} =~ ''.*#respins#.*'') ? false : {aprobare_bom} =~ ''.*#aprobat#.*'' or {aprobare_superior} =~ ''.*#aprobat#.*'' or {aprobare_pe_bom} =~ ''.*#aprobat#.*'''
WHERE documenttypeid = (select id from documenttype where name = 'Cerere concediu');
